export const TrendRTT = new Trend('RTT');
export const RateContentOK = new Rate('Content OK');
export const GaugeContentSize = new Gauge('ContentSize');
export const CounterErrors = new Counter('Errors');
export const options = {
  thresholds: {
    // ËÆ°Êï∞Ôºö‰∏çÂÖÅËÆ∏Ë∂ÖËøá 99 Ê¨°ËøîÂõûÈîôËØØÁöÑÂÜÖÂÆπ„ÄÇ
    'Errors': ['count<100'],
    // ËÆ°ÈáèÔºöËøîÂõûÁöÑÂÜÖÂÆπÂøÖÈ°ªÊéßÂà∂Âú® 4000 Â≠óËäÇ‰ª•‰∏ã„ÄÇ
    'ContentSize': ['value<4000'],
    // ÊØîÁéáÔºöÂÜÖÂÆπÂøÖÈ°ªÂú® 95 Ê¨°‰ª•‰∏äËææÂà∞‚ÄúOK‚Äù„ÄÇ
    'Content OK': ['rate>0.95'],
    // Ë∂ãÂäøÔºöÁôæÂàÜ‰ΩçÊï∞„ÄÅÂπ≥ÂùáÂÄº„ÄÅ‰∏≠‰ΩçÊï∞ÂíåÊúÄÂ∞èÂÄºÂøÖÈ°ª‰øùÊåÅÂú®ÊåáÂÆöÁöÑÊØ´ÁßíËåÉÂõ¥ÂÜÖ„ÄÇ
    'RTT': ['p(99)<300', 'p(70)<250', 'avg<200', 'med<150', 'min<100'],
  },
};

export default function () {
  const res = http.get('https://test-api.k6.io/public/crocodiles/1/');
  const contentOK = res.json('name') === 'Bert';

  TrendRTT.add(res.timings.duration);
  RateContentOK.add(contentOK);
  GaugeContentSize.add(res.body.length);
  CounterErrors.add(!contentOK);

  sleep(1);
}
