//selectionSort.js
function selectionSort(arr) {
  let minIdx,
    aux,
    len = arr.length;
  for (let i = 0; i < len; i++) {
    minIdx = i;
    for (let j = i + 1; j < len; j++) {
      if (arr[j] < arr[minIdx]) {
        minIdx = j;
      }
    }
    aux = arr[i];
    arr[i] = arr[minIdx];
    arr[minIdx] = aux;
  }
  return arr;
}

// Ex. Usage
